tests:
assert set(two_sum([2,7,11,15], 9)) == {0,1}
assert set(two_sum([3,2,4], 6)) == {1,2}
assert set(two_sum([3,3], 6)) == {0,1}
assert set(two_sum([1,5,3,2], 7)) == {1,3}

assert word_frequency_counter('This is this') == {'this':2, 'is':1}
assert word_frequency_counter('Hello hello HELLO') == {'hello':3}
assert word_frequency_counter('One two one') == {'one':2, 'two':1}
assert word_frequency_counter('') == {}

assert count_anagrams(['eat','tea','tan','ate','nat','bat']) == {('a','e','t'):3, ('a','n','t'):2, ('a','b','t'):1}
assert count_anagrams(['listen','silent','enlist']) == {('e','i','l','n','s','t'):3}
assert count_anagrams(['rat','tar','art']) == {('a','r','t'):3}
assert count_anagrams(['a','b','c']) == {('a',):1, ('b',):1, ('c',):1}
